package Generic.JsonDemo;import com.google.common.collect.Lists;import com.google.gson.Gson;import com.google.gson.JsonArray;import com.google.gson.JsonElement;import com.google.gson.JsonParser;import java.util.List;/** * Created by Tan on 2016/11/9. */public class JsonParse {    public static String parseListToJson(List list) {        Gson s = new Gson();        return s.toJson(list);    }    public static <T> List<T> fromJsonList(String json, Class<T> cls) {        Gson s = new Gson();        List<T> mList = Lists.newArrayList();        JsonArray array = new JsonParser().parse(json).getAsJsonArray();        for (final JsonElement elem : array) {            mList.add(s.fromJson(elem, cls));        }        return mList;    }}