package List;import Bean.Person;import com.google.common.collect.Lists;import java.util.Collections;import java.util.Comparator;import java.util.List;/** * Created by Tan on 2016/12/6. */public class ListSort {    public static void main(String... args) {        List<Person> personList = Lists.newArrayList();        Person person = new Person();        person.setSex("男");        person.setAge(44);        person.setName("test1");        Person person1 = new Person();        person1.setSex("男");        person1.setAge(21);        person1.setName("test2");        Person person2 = new Person();        person2.setSex("男");        person2.setAge(33);        person2.setName("test2");        Person person3 = new Person();        person3.setSex("男");        person3.setAge(26);        person3.setName("test2");        Person person4 = new Person();        person4.setSex("男");        person4.setAge(22);        person4.setName("test2");        personList.add(person);        personList.add(person1);        personList.add(person2);        personList.add(person3);        personList.add(person4);        for (Person p : personList) {            System.out.println(p.toString());        }        System.out.println("------------ 分割线 ----------------");        Collections.sort(personList, new Comparator<Person>() {            @Override            public int compare(Person o1, Person o2) {                int flag = 0;                if (o1.getAge() > o2.getAge()) {                    flag = 1;                } else if (o1.getAge() < o2.getAge()) {                    flag = -1;                }                return flag;            }        });        for (Person p : personList) {            System.out.println(p.toString());        }    }}